var start_time=[];
var delay_time=[];

function Generate_caption(){
   	var url_string = window.location.href;
	var url = new URL(url_string);
	var id = url.searchParams.get("id");
	
    for(i=0;i<captions[id].length;i++){
        // document.write('<a href="javascript:void(0);" onClick="player.seekTo('+captions.en[i].start+')"><span class="glyphicon glyphicon-play-circle"></span>'+captions.en[i].text+'</a><br>');
        var duration = captions[id][i].dur * 1000;
		document.write('<div id='+i+'><a href="javascript:void(0);" onClick="player.seekTo('+captions[id][i].start+');stop('+duration+','+i+');"><span class="glyphicon glyphicon-play-circle"></span></a>');
        document.write(captions[id][i].text+'</div>');
		start_time[i] = captions[id][i].start * 1000;
		delay_time[i] = duration;
	}
	// change_caption_color();
	
}
