//duration 1000 = 1 sec.
function stop(duration,i) {
	var origin_text = document.getElementById(i).innerHTML;
	document.getElementById(i).innerHTML = '<span class="playing_text">'+document.getElementById(i).innerHTML+'</span>';
	setTimeout(function(){
		player.pauseVideo();
		document.getElementById(i).innerHTML = origin_text;
	}, duration);
}

